yl4 = sqrt(4 - (1 - xl4) ** 2)
l4 = mpc(xl4, yl4)
p4 = (l4 + l5) / 2
p6 = cu(l4, l7)
p3 = cu(l3, l4)
l2 = cu(p4, p2)
l1 = p3 + 1
l6 = cu(p5, p6)
