vertices = (l1, p1, l2, p2, l3, p3, l4, p4, l5, p5, l6, p6, l7, p7)
# Edges are simply pairs of vertex indices
edges = [(i, (i + 1) % 14) for i in range(14)] + [(2 * i, (2 * i + 5) % 14) for i in range(7)]
